#include "rMatrix.h"rVector rMatrix_3_3::operator*(rVector v) {	rVector t;	t.set (	mCol1.x*v.x + mCol1.y*v.y + mCol1.z*v.z,	    	mCol2.x*v.x + mCol2.y*v.y + mCol2.z*v.z,	    	mCol3.x*v.x + mCol3.y*v.y + mCol3.z*v.z);	return t;}rVector rMatrix_3_3::operator*(rPoint v) {	rVector t;	t.set (	mCol1.x*v.x + mCol1.y*v.y + mCol1.z*v.z,	    	mCol2.x*v.x + mCol2.y*v.y + mCol2.z*v.z,	    	mCol3.x*v.x + mCol3.y*v.y + mCol3.z*v.z);	return t;}